import Navigation from '../Components/Navigation'
import { useRef, useState, useEffect } from 'react'
import {faCheck, faTimes, faInfoCircle} from "@fortawesome/free-solid-svg-icons"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'    
import google from '../Media/google.png'
import "../styles.css"

const user_Regex = /^[a-zA-Z][a-zA-Z0-9-_]{3,23}$/
const password_Regex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%]).{8,24}$/


export default function Register(){
    const userRef = useRef();
    const errRef = useRef();

    const [user, setUser]= useState('');
    const [validName, setValidName] =useState(false);
    const [userFocus, setUserFocus] =useState(false);

    const [pass, setPass]= useState('');
    const [validPass, setValidPass] =useState(false);
    const [passFocus, setPassFocus] =useState(false);

    const [matchPass, setMatchPass]= useState('');
    const [validMatch, setValidMatch] =useState(false);
    const [matchFocus, setMatchFocus] =useState(false);

    const [errMsg, setErrMsg] =useState('');
    const [success, setSuccess] =useState(false);

    useEffect(()=>{
        userRef.current.focus();
    },[])


    useEffect(()=>{
        const result=user_Regex.test(user);
        console.log(user);
        console.log(result);
        setValidName(result);
    },[user])

    useEffect(()=>{
        const result=password_Regex.test(pass);
        console.log(pass);
        console.log(result);
        setValidPass(result);
        const match = pass === matchPass;
        setValidMatch(match)
      
    },[pass,matchPass])

    useEffect(()=>{
        setErrMsg('');
    },[user,pass,matchPass])

    return(
        <>
        <div>
            <Navigation />
            <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive">{errMsg}</p>
            <h1 className="pageTitle">Registration Page</h1>
        </div>
        <br/>
        <div className='login'>
            <form>
                
                <label htmlFor="username">
                    Email:
                </label>
                <input 
                className='loginInput' 
                type='text'
                ref={userRef}
                id="username" 
                autoComplete='off' 
                onChange={(e) => setUser(e.target.value)} 
                required  
                aria-invalid={validName ? "false" : "true"}
                aria-describedby='uidnote'
                placeholder='Email...'
                onFocus={() => setUserFocus(true)}
                onBlur={() => setUserFocus(true)}
                />

                <p id="uidnote" className={userFocus && user &&
                     !validName ? "instructions" : "offscreen"}>
                <FontAwesomeIcon icon={faInfoCircle} />
                Must be 4 to 24 characters.<br/>
                Must also begin with a letter.<br/>
                Can include letters, numbers, underscores and hyphens.
                </p>

                <br/>

                <input 
                className='loginInput'
            
                type='text' 
                placeholder='Password...'/><br/>
                <button className='loginButton'>Sign Up</button><br/>
                <h3 >OR</h3>
                <img src={google} alt="GoogleLogin" className='googlelogin'/>
            </form>
        </div>
        </>
      
    )
}
