import React, { useState } from 'react';

const DropdownMenu = () => {
  const [selectedOptions, setSelectedOptions] = useState([]);

  const handleChange = (event) => {
    const optionValue = event.target.value;
    const newSelectedOptions = [...selectedOptions];

    if (event.target.checked) {
      newSelectedOptions.push(optionValue);
    } else {
      const index = newSelectedOptions.indexOf(optionValue);
      if (index > -1) {
        newSelectedOptions.splice(index, 1);
      }
    }

    setSelectedOptions(newSelectedOptions);
  };

  return (
    <div>
      <div>
        <label>
          <input type="checkbox" value="option1" onChange={handleChange} /> Option 1
        </label>
      </div>
      <div>
        <label>
          <input type="checkbox" value="option2" onChange={handleChange} /> Option 2
        </label>
      </div>
      <div>
        <label>
          <input type="checkbox" value="option3" onChange={handleChange} /> Option 3
        </label>
      </div>
      <div>
        <label>
          <input type="checkbox" value="option4" onChange={handleChange} /> Option 4
        </label>
      </div>
      
      <div>
        <h3>Selected Options:</h3>
        <ul>
          {selectedOptions.map((option, index) => (
            <li key={index}>{option}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default DropdownMenu;
