import React, { useState } from 'react'
import '../styles.css';

export default function Message(){


    const [inputdata, Setinputdata]=useState({
        sender:"",
        message:""
    })

    const [inputarr, Setinputarr]=useState([])

    function changehandle(e){
            
        Setinputdata({...inputdata,[e.target.name]:e.target.value})
    }

    let {sender,message}=inputdata;

    function changhandle(){
        Setinputarr([...inputarr,{sender,message}])
    }
    return(
        <>
        <div className='msginput'>
            <input type="text" placeholder='Sender...' autoComplete='off' name="sender" value={inputdata.sender} onChange={changehandle}/> <br/>
            <input className="message" type="text" autoComplete='off' name="msg" placeholder='Message...' value={inputdata.message} onChange={changehandle}/>
            <button onClick={changhandle}>Send</button>
        </div>
        </>
    )
}
